import React, { useState, useEffect, useRef } from "react";
import "./WeatherApp.css";
import { Input } from "./Input";
import { Button } from "./Button";
import axios from "axios";
import WeeklyData from "./shared/WeeklyData";
import CurrentData from "./shared/CurrentData";
import Chart from "./shared/Chart";
import moment from "moment";
import GoogleMap from "./shared/GoogleMap";
import Accordion from "./shared/Accordion";

function CurrentWeather(props) {
  const [search, setSearch] = useState("");
  const [weeklyWeather, setWeeklyWeather] = useState([]);
  const ref = useRef();
  const [xAxis, setXais] = useState([]);
  const [yAxis, setYaxis] = useState([]);
  // const [chartDescription, setchartDescription] = useState([]);
  // const [newChartDes, setNewChartDes] = useState([]);

  // Accordion
  const [setActive, setActiveState] = useState("");
  const [setHeight, setHeightState] = useState("0px");
  const [setRotate, setRotateState] = useState("accordion__icon");

  const content = useRef(null);

  const toggleAccordion = () => {
    setActiveState(setActive === "" ? "active" : "");
    setHeightState(
      setActive === "active" ? "0px" : `${content.current.scrollHeight}px`
    );
    setRotateState(
      setActive === "active" ? "accordion__icon" : "accordion__icon rotate"
    );
    // console.log(content.current.scrollHeight);
  };

  const [data, setData] = useState({
    temp: 0,
    minTemperature: [],
    maxTemperature: [],
    humidity: "",
    description: "",
    icon: "",
    display: false,
    pressure: "",
    feelsLike: 0,
    visibility: "",
    wind: 0,
    name: "",
  });

  const [geolocation, setGeolocation] = useState({
    lat: "",
    log: "",
  });

  useEffect(() => {
    if (search === "") {
      navigator.geolocation.getCurrentPosition(function (position) {
        setGeolocation({
          ...geolocation,
          lat: position.coords.latitude,
          log: position.coords.longitude,
        });
      });
    }
    if (navigator.geolocation) {
      navigator.geolocation.watchPosition(function (position) {
        setGeolocation({
          ...geolocation,
          lat: position.coords.latitude,
          log: position.coords.longitude,
        });
      });
    }
  }, [geolocation.lat, geolocation.log]);

  useEffect(() => {
    if (geolocation.lat && geolocation.log) {
      fetchData();
    }
  }, [geolocation.lat, geolocation.log]);

  const fetchWeatherData = async (search) => {
    try {
      const response = await axios({
        method: "GET",
        url: `http://api.openweathermap.org/data/2.5/weather?q=${search}&appid=35bd10c223b33cc321caa26ce46f7f0b`,
      });
      console.log("res====",response.data.coord)
      setData({
        temp: response.data.main.temp - 273.15,
        humidity: response.data.main.humidity,
        description: response.data.weather[0].description,
        icon: response.data.weather[0].icon,
        display: true,
        pressure: response.data.main.pressure,
        feelsLike: response.data.main.feels_like - 273.15,
        visibility: response.data.visibility,
        wind: response.data.wind.speed,
        name: response.data.name,
      });
    } catch (error) {
      console.error(error.message);
    }
  };

  console.log("lat====", geolocation)
  const fetchData = async (search) => {
    const apikey = `https://api.openweathermap.org/data/2.5/onecall?lat=${geolocation.lat}&lon=${geolocation.log}&units=metric&exclude`;
    const response = await fetch(
      `${apikey}=current&appid=3c072250b7b95a5b2c1f101aabc82b3a`
    );
    const dailyForcast = await fetch(
      `${apikey}=hourly,daily&appid=3c072250b7b95a5b2c1f101aabc82b3a`
    );
    const data = await response.json();
    const dailyData = await dailyForcast.json();
    setWeeklyWeather(data.daily);
    setXais(data.hourly);
    setYaxis(data.hourly);
    // setchartDescription(data.hourly);
    // setNewChartDes(chartDescriptionData);

    // console.log("current tem", dailyData.current);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    fetchWeatherData(search);
    fetchData();
    setSearch((prev) => prev);
  };

  useEffect(() => {
    ref.current = setSearch;
  }, []);

  const newXAxisData =
    xAxis &&
    xAxis.map((res) => {
      const day = moment.unix(res.dt).utc();
      return moment(day).format("h:mm a");
    });
  const newYAxisData =
    yAxis &&
    yAxis.map((res) => {
      return res.temp;
    });
  // const chartDescriptionData =
  //   chartDescription &&
  //   chartDescription.map((res) => {
  //     return res.weather;
  //   });

  // const newChartDescriptionData =
  //   newChartDes &&
  //   newChartDes.map((res) => {
  //     return res.description;
  //   });
  // console.log("chart description data", newChartDescriptionData);
  return (
    <div>
      <div className="app__header">
        <form className="search__filter" onSubmit={handleSubmit}>
          <div className="input__group">
            <Input
              className="search__filed"
              type="search"
              value={search}
              onChange={(e) => setSearch(e.target.value)}
              placeholder="Search City"
            ></Input>
            <Button type="submit" className="search__btn" label={"Search"} />
          </div>
        </form>
      </div>
      <div className="main__container">
        <div className="grid__container">
          <div className="current__container">
            <CurrentData display={data.display} data={data} />
          </div>
          <div className="map__section">
            <GoogleMap isMarkerShown data={data} />
          </div>
        </div>
      </div>

      <div className="inner__container">
        <Chart
          newXAxisData={newXAxisData}
          newYAxisData={newYAxisData}
          dataa={data}
        />
        <WeeklyData weeklyWeather={weeklyWeather} data={data} />
      </div>
      <Accordion
        title="What is your return policy?"
        content="Lorem Ipsum is simply dummy text of the printing and typesetting industry."

      />
    </div>
  );
}

export default CurrentWeather;
