import React, {useRef} from "react";
import "./Accordion.css";
import ChavronIcon from "./shared/ChavronIcon";
import "./shared/Accordion.css";

function Accordion(props) {
  const content = useRef(null);
  const [setActive, setActiveState] = useState("");
  const [setHeight, setHeightState] = useState("0px");
  const [setRotate, setRotateState] = useState("accordion__icon");
  return (
    <div className="accordion__section">
      <button
        type="button"
        className={`accordion ${setActive}`}
        onClick={toggleAccordion}
      >
        <h4 className="accordion__title">{props.title}</h4>
        <ChavronIcon className={`${setRotate}`} width={10} fill={"#777"} />
      </button>
      <div
        ref={content}
        style={{ maxHeight: `${setHeight}` }}
        className="accordion__content"
      >
        <div className="accordion__text">{props.content}</div>
      </div>
    </div>
  );
}

export default Accordion;
