import React, { useState, useRef } from "react";
import "./Accordion.css";
import ChavronIcon from "./ChavronIcon";
import "./Accordion.css";

function Accordion({ data }) {
  const [setActive, setActiveState] = useState("");
  const [setHeight, setHeightState] = useState("0px");
  const [setRotate, setRotateState] = useState("accordion__icon");

  const content = useRef(null);

  const toggleAccordion = () => {
    setActiveState(setActive === "" ? "active" : "");
    setHeightState(
      setActive === "active" ? "0px" : `${content.current.scrollHeight}px`
    );
    setRotateState(
      setActive === "active" ? "accordion__icon" : "accordion__icon rotate"
    );
  };

  return (
    <div className="accordion__section">
      <button
        type="button"
        className={`accordion ${setActive}`}
        onClick={toggleAccordion}
      >
       {weeklyWeather &&
          weeklyWeather.map((value, i) => {
            const day = moment.unix(value.dt).utc();
            if (i <= 8) {
              return (
                <li className="grid-item" key={i}>
                  <div className="day content">
                    <span className="gutter__right">
                      {moment(day).format("dddd")},
                    </span>
                    <span>{moment(day).format("MMMM Do, h:mm a")}</span>
                  </div>
                  <div className="day__list__values">
                    <img
                      className="owm_weather_icon"
                      src={`http://openweathermap.org/img/wn/${value.weather[0].icon}@2x.png`}
                      alt="weather-icon"
                      width="200"
                      height="200"
                    />
                    {value.temp.max}/{value.temp.min}â„ƒ
                    <div className="weather content">{data.description}</div>
                  </div>
                </li>
              );
            }
          })}
        <h4 className="accordion__title">{data.description}</h4>
        <ChavronIcon className={`${setRotate}`} width={10} fill={"#777"} />
      </button>
      <div
        ref={content}
        style={{ maxHeight: `${setHeight}` }}
        className="accordion__content"
      >
        <div className="accordion__text">{content}</div>
      </div>
    </div>
  );
}

export default Accordion;
