import React, { useState, useRef } from "react";
import "./Accordion.css";
import ChavronIcon from "./ChavronIcon";
import "./Accordion.css";
import moment from "moment";

function Accordion(props) {
  console.log("days data=====", props.weeklyWeather);
  const [setRotate, setRotateState] = useState("accordion__icon");
  const { activeState, setActiveState } = props;

  const [dailyWeatherDetails, setDailyWeatherDetails] = useState({
    weatherIcon: "",
    daliyMinTemp: [],
    daliyMaxTemp: [],
    dailyDescription: "",
    clouds: "",
    wind_speed: "",
    pressure: "",
    humidity: "",
    uvi: "",
    dew_point: "",
  });

  const [activeDate, setActiveDate] = useState("");

  const content = useRef(null);

  const toggleAccordion = () => {
    setActiveState(!activeState);
    setRotateState(
      activeState === "active" ? "accordion__icon" : "accordion__icon rotate"
    );
  };

  const detailsHandler = (e) => {
    console.log("event==", e);
    setDailyWeatherDetails({
      weatherIcon: e.weather[0].icon,
      daliyMinTemp: e.temp.min,
      daliyMaxTemp: e.temp.max,
      dailyDescription: e.weather[0].description,
      clouds: e.clouds,
      wind_speed: e.wind_speed,
      pressure: e.pressure,
      humidity: e.humidity,
      uvi: e.uvi,
      dew_point: e.dew_point,
    });
    setActiveDate(e.dt);
  };

  console.log("activeDate=====", activeDate);

  return (
    <div className="accordion__section">
      <div className="grid-item">
        <div className={`accordion ${activeState}`}>
          <ul className="days_inner_list">
            {props.weeklyWeather
              ? props.weeklyWeather.map((value, i) => {
                  const day = moment.unix(value.dt).utc();
                  if (i <= 8) {
                    console.log("value.dt=====", day);
                    return (
                      <>
                        <li
                          className={`day_content ${
                            value.dt === activeDate ? "active" : ""
                          }`}
                          onClick={() =>
                            value.dt === activeDate
                              ? toggleAccordion()
                              : detailsHandler(value)
                          }
                        >
                          <span className="gutter__right">
                            {moment(day).format("ddd")},
                          </span>
                          <span>{moment(day).format("MMMM Do")}</span>
                        </li>
                      </>
                    );
                  }
                })
              : null}
          </ul>
          <ChavronIcon
            className={`${setRotate}`}
            width={15}
            height={15}
            fill={"#48484A"}
            onClick={() => toggleAccordion()}
          />
        </div>
        <div className="scrolling_container_content">
          {dailyWeatherDetails.weatherIcon ? (
            <>
              <div className="top__section">
                <img
                  className="owm_weather_icon"
                  src={`http://openweathermap.org/img/wn/${dailyWeatherDetails.weatherIcon}@2x.png`}
                  alt="weather-icon"
                  width="200"
                  height="200"
                />
                <div className="inner_content">
                  <p>{dailyWeatherDetails.dailyDescription}</p>
                  <p>
                    The high will be{" "}
                    {Math.floor(dailyWeatherDetails.daliyMaxTemp)}
                    ℃, the low will be{" "}
                    {Math.floor(dailyWeatherDetails.daliyMinTemp)}℃.
                  </p>
                </div>
              </div>

              <ul className="weather__items">
                <li>{dailyWeatherDetails.clouds}%</li>
                <li>{dailyWeatherDetails.wind_speed} m/s WNW</li>
                <li>{dailyWeatherDetails.pressure}hPa</li>
                <li>Humidity: {dailyWeatherDetails.humidity}%</li>
                <li>UV: {dailyWeatherDetails.uvi}</li>
                <li>Dew point: {dailyWeatherDetails.dew_point}</li>
              </ul>
              <table>
                <tr>
                  <th></th>
                  <th>Company</th>
                  <th>Contact</th>
                  <th>Country</th>
                </tr>
                <tr>
                  <td>Alfreds Futterkiste</td>
                  <td>Maria Anders</td>
                  <td>Germany</td>
                </tr>
                <tr>
                  <td>Centro comercial Moctezuma</td>
                  <td>Francisco Chang</td>
                  <td>Mexico</td>
                </tr>
              </table>
            </>
          ) : (
            <div className="message">Select above date</div>
          )}
        </div>
      </div>

      {/* <h4 className="accordion__title">{props.title}</h4> */}

      <div ref={content} className="accordion__content">
        <div className="accordion__text"></div>
      </div>
    </div>
  );
}

export default Accordion;
