import React, { useState } from "react";
import moment from "moment";
import Accordion from "./Accordion";
import "./Accordion.css";
import ChavronIcon from "./ChavronIcon";

function WeeklyData({ weeklyWeather, data }) {
  // console.log("data", data);
  const [activeState, setActiveState] = useState("");
  const [isActive, setIsActive] = useState(false);

  const toggleAccordion = () => {
    setActiveState(activeState === "" ? "active" : "");
    setHeightState(
      activeState === "active" ? "0px" : `${content.current.scrollHeight}px`
    );
    setRotateState(
      activeState === "active" ? "accordion__icon" : "accordion__icon rotate"
    );
    // console.log(content.current.scrollHeight);
  };

  return (
    <div className="weekly_weather_data">
      <h3>8-day forecast</h3>
      <ul className={`day__list ${activeState}`}>
        {weeklyWeather
          ? weeklyWeather.map((value, i) => {
              const day = moment.unix(value.dt).utc();
              if (i <= 8) {
                return (
                  <li className="grid-item" key={i}>
                    <div className="inner_list_content">
                      <div className="day content">
                        <span className="gutter__right">
                          {moment(day).format("dddd")},
                        </span>
                        <span>{moment(day).format("MMMM Do, h:mm a")}</span>
                      </div>
                      <div className="day__list__values">
                        <img
                          className="owm_weather_icon"
                          src={`http://openweathermap.org/img/wn/${value.weather[0].icon}@2x.png`}
                          alt="weather-icon"
                          width="200"
                          height="200"
                        />
                        {value.temp.max}/{value.temp.min}â„ƒ
                        <div className="weather__content">
                          <button
                            type="button"
                            className="weather_description"
                            onClick={toggleAccordion}
                          >
                            {data.description}{" "}
                            <ChavronIcon
                              className=""
                              width={10}
                              fill={"#777"}
                            />
                          </button>
                        </div>
                      </div>
                    </div>
                    {isActive && (
                      <Accordion
                        title={data.description}
                        content="Lorem Ipsum is simply dummy text of the printing and typesetting industry."
                        setActiveState={setActiveState}
                        activeState={activeState}
                      />
                    )}
                  </li>
                );
              }
            })
          : null}
      </ul>
    </div>
  );
}

export default WeeklyData;
