import React, { useState } from "react";
import Accordion from "./Accordion";
import "./Accordion.css";
import WeeklyDetailsList from "./WeeklyDetailsList";
import {BeatLoader} from "react-spinners";


function WeeklyData({ weeklyWeather, data }) {
  const [activeState, setActiveState] = useState("");
  const [activeClass, setActiveClass] = useState("")
  const [loading, setLoading] = useState(true);

  const toggleClass = (e) => {
    e.preventDefault();
    setActiveClass(activeState === "" ? "active" : "");
  };

  return (
    <div className="weekly_weather_data">
      <h3>8-day forecast</h3>
      {setLoading ? (
        <div>
          <WeeklyDetailsList
            activeState={activeState}
            weeklyWeather={weeklyWeather}
            toggleClass={toggleClass}
            data={data}
          />
          {activeState && (
            <Accordion
              title={data.description}
              content="Lorem Ipsum is simply dummy text of the printing and typesetting industry."
              setActiveState={setActiveClass}
              activeState={activeClass}
              weeklyWeather={weeklyWeather}
              data={data}
            />
          )}
        </div>
      ) : (
        <div>
          <BeatLoader
           className="Loader"
           size={25}
           color='red'
           loading={loading}
          />
        </div>
      )}
    </div>
  );
}

export default WeeklyData;
