import React, { useState, useRef } from "react";
import "./Accordion.css";
import ChavronIcon from "./ChavronIcon";
import "./Accordion.css";
import moment from "moment";

function Accordion(props) {
  console.log("days data=====", props.weeklyWeather);
  const [setRotate, setRotateState] = useState("accordion__icon");
  const { activeState, setActiveState } = props;
  const [clickedItem, setClickedItem] = useState(null);

  const content = useRef(null);

  const handleCSS = (e) => {
    e.preventDefault();
    let selectedTag = e ? parseInt(e.target.id, 10) : null;
    setClickedItem(selectedTag);
    console.log(">> clickedItem", clickedItem);
  };

  const toggleAccordion = () => {
    setActiveState(!activeState);
    setRotateState(
      activeState === "active" ? "accordion__icon" : "accordion__icon rotate"
    );
  };

  return (
    <div className="accordion__section">
      <div className="grid-item">
        <button
          type="button"
          className={`accordion ${activeState}`}
          onClick={toggleAccordion}
        >
          <ul className="days_inner_list">
            {props.weeklyWeather
              ? props.weeklyWeather.map((value, i) => {
                  const day = moment.unix(value.dt).utc();
                  if (i <= 8) {
                    return (
                      <>
                        <li
                          className={`day_content`}
                          key={index}
              onClick={handleCSS}
              id={index}
              className={`day_content {}`}
                        >
                          <span className="gutter__right">
                            {moment(day).format("ddd")},
                          </span>
                          <span>{moment(day).format("MMMM Do")}</span>
                        </li>
                      </>
                    );
                  }
                })
              : null}
          </ul>
          <ChavronIcon
            className={`${setRotate}`}
            width={15}
            height={15}
            fill={"#48484A"}
          />
        </button>
        <div className="scrolling_container_content">
          {props.weeklyWeather
            ? props.weeklyWeather.map((value, i) => {
                const day = moment.unix(value.dt).utc();
                if (i <= 8) {
                  return (
                    <div className="day__list__values">
                      <img
                        className="owm_weather_icon"
                        src={`http://openweathermap.org/img/wn/${value.weather[0].icon}@2x.png`}
                        alt="weather-icon"
                        width="200"
                        height="200"
                      />
                      {value.temp.max}/{value.temp.min}â„ƒ
                    </div>
                  );
                }
              })
            : null}
        </div>
      </div>

      {/* <h4 className="accordion__title">{props.title}</h4> */}

      <div ref={content} className="accordion__content">
        <div className="accordion__text"></div>
      </div>
    </div>
  );
}

export default Accordion;
